mapper.select:
SELECT id, username, password, email, role, createdAt, enabled
FROM user_table
WHERE username = #{username}

SELECT id, username, password, email, role, createdAt, enabled
FROM user_table
WHERE id = #{id}

mapper.update:
UPDATE user_table
SET email = #{email},
    role = #{role},
    enabled = #{enabled}
WHERE id = #{id}
